import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logoImg from '../../assets/images/logo.svg';
import landingImg from '../../assets/images/landing.svg';
import studyIcon from '../../assets/images/icons/study.svg';
import giveClassesIcon from '../../assets/images/icons/give-classes.svg';
import purpleHearthIcon from '../../assets/images/icons/purple-heart.svg';

import './styles.css';

function Landing() {
    const [totalConnections, setTotalConnections] = useState(0);

    return (
        <div id="page-landing">
            <div id="page-landing-content" className="container">
                <div className="logo-container">
                    <img src={logoImg} alt="Logo"/>
                    <h2>Sua plataforma de estudos online.</h2>
                </div>
                <img src={landingImg} alt="Imagem plataforma" className="hero-image"/>

                <div className="buttons-container">
                    <Link to="/study" className="study">
                        <img src={studyIcon} alt="Estudar icone"/>
                        <p>Estudar</p>
                    </Link>
                    
                    <Link to="/give-classes" className="give-classes">
                        <img src={giveClassesIcon} alt="Dar aulas icone"/>
                        <p>Dar aulas</p>
                    </Link>
                </div>

                <span className="total-connections">
                        Total de 200 conexões já realizadas <img src={purpleHearthIcon} alt="Coração roxo"/>
                </span>
            </div>
        </div>
    );
}

export default Landing
